local chargeFovAdjustment = 15function Onos:GetThirdPersonOffset()  local z = -1.6 - self:GetVelocityLength() / self:GetMaxSpeed(true) * 0.4  return Vector(-1.4, 0.3, z)endfunction Onos:GetFirstPersonFov()  return kOnosFovendfunction Onos:GetThirdPersonFov()  return kDefaultFov + self:GetChargeFraction() * chargeFovAdjustmentendfunction Onos:GetTooCloseDistance()  return 0.4endlocal originalPlayerCameraCoordsAdjustment = Onos.PlayerCameraCoordsAdjustmentfunction Onos:PlayerCameraCoordsAdjustment(cameraCoords)    local coords = originalPlayerCameraCoordsAdjustment(self, cameraCoords)    return Alien.PlayerCameraCoordsAdjustment(self, coords)    end-- TODO: put this in a different file...function Gore:GetMeleeBase()    -- Width of box, height of box    return 2, 1.4end