local old_PlayFootstepsShake = Onos._PlayFootstepShake-- Shake camera for for given playerfunction Onos:_PlayFootstepShake(player, scalar)        local isThirdPersonSelf = (player ~= nil and player:GetIsAlive() and player == self and player:GetIsThirdPerson())    if player ~= nil and player:GetIsAlive() and (player ~= self or isThirdPersonSelf) then                local kMaxDist = 25                local dist = (player:GetOrigin() - self:GetOrigin()):GetLength()                if dist < kMaxDist then                    local amount = (kMaxDist - dist)/kMaxDist                        -- magic numbers            local shakeAmount = .002 + amount * amount * .004            local shakeSpeed = 2 + amount * amount * 9            local shakeTime = .4 - (amount * amount * .3)                        -- override for thirdperson            if isThirdPersonSelf then              shakeAmount = shakeAmount * 0.5            end                        player:SetCameraShake(shakeAmount * scalar, shakeSpeed, shakeTime)                    end            end        end